const menuBtn=document.querySelector(".burger-menu-btn"),headerSection=document.querySelector(".header"),allLinks=document.querySelectorAll("a");menuBtn.addEventListener("click",(()=>{headerSection.classList.toggle("menu--open")})),allLinks.forEach((e=>{e.addEventListener("click",(()=>{e.classList.contains("menu__link")&&headerSection.classList.toggle("menu--open")}))}));const slider=document.querySelector(".slider"),buttonPrev=slider.querySelector(".slider__arrows-prev"),buttonNext=slider.querySelector(".slider__arrows-next"),buttonBack=slider.querySelector(".slider__arrows-back"),buttonsOpen=slider.querySelectorAll(".slider__open"),sliderItemBox=slider.querySelector(".slider__items");let unAcceptClick;function showSlider(e){slider.classList.remove("next","prev");const t=slider.querySelectorAll(".slider__item");if("next"===e)sliderItemBox.appendChild(t[0]),slider.classList.add("next");else{const e=t.length-1;sliderItemBox.prepend(t[e]),slider.classList.add("prev")}}buttonNext.addEventListener("click",(()=>{showSlider("next")})),buttonPrev.addEventListener("click",(()=>{showSlider("prev")})),buttonsOpen.forEach((e=>{e.addEventListener("click",(()=>{slider.classList.add("show-detail")}))})),buttonBack.addEventListener("click",(()=>{slider.classList.remove("show-detail")}));